mapper.insert:
	SELECT NVL(MAX(bno), 1000) + 1
	FROM board

INSERT INTO board
	(
		bno
		, title
		, contents
		, writer
		, image
	)
VALUES
	(
		#{bno}
		,#{title}
		,#{contents}
		,#{writer}
		,#{image}
	)

mapper.update:
UPDATE board
	set
		title = #{title}
		, contents = #{contents}
		, writer = #{writer}
		, updatedate = sysdate
		, image = #{image}
WHERE bno = #{bno}
