mapper.insert:
SELECT NVL(MAX(employee_id),0) +1
FROM employees

insert into employees
	(
		employee_id
		, first_name
		, last_name
		, email
		, hire_date
		, job_id

		, salary

	)
values
(
	#{employeeId}
	,#{firstName}
	,#{lastName}
	,#{email}


	,#{hireDate}


		,sysdate


	,#{jobId}

	,#{salary}

)

mapper.update:
update employees
	set
		first_name = #{firstName}
		, email    = #{email}
		, salary   = #{salary}
where employee_id = #{employeeId}
